Glossary = [{ t: "أفعال ناقصةً ", d: "<br>لأنها تدخل على الجملة الاسمية فتنسخُ حكمها أي تغيره بحكم آخر؛ فترفع المبتدأ، ويسمى اسمها، وتنصب الخبر، ويُسَمّى خبرُها.<br>" }
, { t: "حروفٌ ناسخة", d: "<br>هي التي تدلُّ على الزمان فقط، دون الدلالة على الحدث.	<br>" }
, { t: "التوكيد", d: "<br>لأنها تدخل على الجملة الاسمية فتنسخُ حكمها أي تغيره بحكم آخر؛ فتنصب المبتدأ، ويسمى اسمها، وترفع الخبر، ويُسَمّى خبرُها. <br>" }
, { t: "التوكيد اللفظي", d: "<br>أسلوب من أساليب اللغة العربية يفيد تثبيت المعنى وإزالة اللبس، وهو نوعان:  توكيد لفظي، وتوكيد معنوي<br>" }
, { t: "التلخيص", d: "<br>يعني تكرار اللفظ نفسه؛ سواء أكان المكرر جملة أم مفرداً؛ والمفرد يكون اسماً أو فعلاً أو حرفاً.<br>" }
, { t: "الخاطرة", d: "<br>التعبير عن الأفكار الأساسية للموضوع في كلمات قليلة، مع عدم  الإخلال بالمضمون  أو الصياغة <br>" }
, { t: "المقال", d: "<br>نص أدبي  قصير يعرض فيه الكاتب فكرة حول موضوع ما، ويخلو من كثرة التفصيلات<br>" }
, { t: "المقال الأدبي", d: "<br>نص نثري  متوسط الطول يبحث في موضوع معين بطريقة سهلة، وأسلوب جميل<br>" }
, { t: "المقال العلمي", d: "<br>نص نثري  متوسط الطول يبحث في موضوع معين بطريقة سهلة، وأسلوب جميل وتظهر فيه شخصية الكاتب واضحة، وتتميز بالأسلوب الأدبي الذي يعتمد على العاطفة، ويستخدم الخيال، ويهتم بالأسلوب البياني، وانتقاء الألفاظ<br>" }
, { t: "علامات الترقيم", d: "<br>نص نثري  متوسط الطول يبحث في موضوع معين بطريقة سهلة، وأسلوب جميل ولا تظهر فيه شخصية الكاتب، ويبرز فيه الأسلوب العلمي الذي يركز على الفكرة والموضوع بشكل أساسي، ويقدم الأدلة والبراهين، ويعتمد على الشرح والتفسير<br>" }
]
intro = true;
